CREATE VIEW `vw_operation_with_category` AS select "operation"."id", "operation"."name", "operation"."description", "operation"."value", "operation"."date", "operation"."is_paid", "operation"."is_income", "operation"."category_id", "category"."name" from "operation" left join "category" on operation.category_id = category.id;