DROP VIEW `vw_operation_with_category`;--> statement-breakpoint
CREATE VIEW `vw_operation_with_category` AS select "operation"."id", "operation"."name", "operation"."description", "operation"."value", "operation"."date", "operation"."is_paid", "operation"."is_income", "operation"."category_id", category.name as "category_name", "operation"."payment_method_id", payment_method.name as "payment_method_name" from "operation" left join "category" on operation.category_id = category.id left join "payment_method" on operation.payment_method_id = payment_method.id;