CREATE VIEW `vw_payment_method_balance` AS select "payment_method_id", "payment_method_name", CAST(SUM(value) AS INTEGER) as "total" from "vw_expense_with_category" where date(date, 'unixepoch', '-3 hours') >= date('now', '-3 hours', 'start of month') AND date(date, 'unixepoch', '-3 hours') < date('now', '-3 hours' , '+1 month', 'start of month') group by "vw_expense_with_category"."payment_method_id" order by "vw_expense_with_category"."payment_method_id";